import React, { useState } from 'react';
import './EditUser.css'; // Agrega un archivo CSS para el estilo

const EditUser = () => {
  const [editField, setEditField] = useState(null);

  // Función para manejar la selección del campo de edición
  const handleFieldSelection = (field) => {
    setEditField(field);
  };

  return (
    <div className="login-container">
      <h2>Edit User</h2>
      {editField === null ? (
        // Mostrar opciones de selección de campo
        <div className="field-selection">
          <p>What would you like to edit?</p>
          <button onClick={() => handleFieldSelection('username')}>Username</button>
          <button onClick={() => handleFieldSelection('email')}>Email</button>
          <button onClick={() => handleFieldSelection('password')}>Password</button>
        </div>
      ) : (
        // Mostrar campos de edición correspondientes
        <div className="edit-field">
          <p>Edit {editField}:</p>
          <input
            type={editField === 'password' ? 'password' : 'text'}
            placeholder={`Enter new ${editField}`}
          />
          <button className="edit-button">Save Changes</button>
        </div>
      )}
    </div>
  );
};

export default EditUser;
